package com.shopping.mall.product;

import java.math.BigDecimal;

public class outerInfo {
	private String outer_menu;
	private String outer_name;
	private BigDecimal outer_price;
	private BigDecimal outer_stock;
	private BigDecimal outer_sale;
	private String outer_todaydel;
	
	public outerInfo() {
		// TODO Auto-generated constructor stub
	}

	public String getOuter_menu() {
		return outer_menu;
	}

	public void setOuter_menu(String outer_menu) {
		this.outer_menu = outer_menu;
	}

	public String getOuter_name() {
		return outer_name;
	}

	public void setOuter_name(String outer_name) {
		this.outer_name = outer_name;
	}

	public BigDecimal getOuter_price() {
		return outer_price;
	}

	public void setOuter_price(BigDecimal outer_price) {
		this.outer_price = outer_price;
	}

	public BigDecimal getOuter_stock() {
		return outer_stock;
	}

	public void setOuter_stock(BigDecimal outer_stock) {
		this.outer_stock = outer_stock;
	}

	public BigDecimal getOuter_sale() {
		return outer_sale;
	}

	public void setOuter_sale(BigDecimal outer_sale) {
		this.outer_sale = outer_sale;
	}

	public String getOuter_todaydel() {
		return outer_todaydel;
	}

	public void setOuter_todaydel(String outer_todaydel) {
		this.outer_todaydel = outer_todaydel;
	}
	
}
